		 (IF(F301.GRNNo IS NULL, FALSE, TRUE) = prmReceivedStatus1 OR IF(F301.GRNNo IS NULL, FALSE, TRUE) = prmReceivedStatus2)
GROUP BY F201.BranchCode, F201.PODNo;
