	INNER JOIN F110 ON F203.WareHouseCode = F110.Code
WHERE 
	LENGTH(F204.GRNNo) = 0 AND
	F203.TransactionDate between prmFirstDate AND prmLastDate
	
	
GROUP BY F203.PRTNo
ORDER BY F203.PRTNo DESC;
END */$$
