in PrmGrandTotal               decimal(18,4),
in PrmCreatedBy                varchar(50)
)
BEGIN
		INSERT INTO F301 (
			BranchCode,
			GRNNo,
			TransactionDate,
			PODNo,
			SupplierCode,
			CurrencyCode,
			ExchangeRate,
			PaymentTermCode,
			WarehouseCode,
			ReceivedBy,
			Description,
			SupplierRefNo,
			PoliceNo,
			ContainerNo,
			TotalTransactionAmount,
			DiscountPercent,
			DiscountAmount,
			PPNPercent,
			PPNAmount,
			FreightFeeAmount,
			InsuranceFeeAmount,
			AdministrationFeeAmount,
			OtherFeeAmount,
			GrandTotal,
			CreatedBy,
			CreatedDate)
		VALUES(
			PrmBranchCode,
			PrmGRNNo,
			PrmTransactionDate,
			PrmPODNo,
			PrmSupplierCode,
			PrmCurrencyCode,
			PrmExchangeRate,
			PrmPaymentTermCode,
			PrmWarehouseCode,
			PrmReceivedBy,
			PrmDescription,
			PrmSupplierRefNo,
			PrmPoliceNo,
			PrmContainerNo,
			PrmTotalTransactionAmount,
			PrmDiscountPercent,
			PrmDiscountAmount,
