			PrmPPNAmount,
			PrmFreightFeeAmount,
			PrmInsuranceFeeAmount,
			PrmAdministrationFeeAmount,
			PrmOtherFeeAmount,
			PrmGrandTotal,
			PrmCreatedBy,
			NOW() );
    END */$$
