		INNER JOIN F109 on  F507.SupplierCode = F109.Code
		Inner Join F006 On F507.CurrencyCode = F006.Code
		
	Where 
		-- F507.BranchCode = prmBranchCode AND 
		F507.TransactionDate BETWEEN prmFirstDate AND prmLastDate
