     Inner Join F001 On F203.BranchCode = F001.Code
     Inner Join F006 On F203.CurrencyCode = F006.Code
     Inner Join F109 On F203.SupplierCode = F109.Code
     Inner Join F110 On F203.WarehouseCode = F110.Code
