	qr2.AccountName,
	cast(0 as signed)  as LastMonthIDR,
	sum(qr2.DebitamountIDR - qr2.CreditAmountIDR)  as ThisMonthIDR
	from(
	select 
	* from( 
	select 
	F401.VoucherNo					as VoucherNo, 
	F401.TransactionDate				as TransactionDate, 
	F401.RefNo					as RefNo, 
	F401.ExchangeRate				as ExchangeRate, 
